"""Domain models scaffold generated by ANG."""

from __future__ import annotations

from datetime import datetime
from typing import Any

from pydantic import BaseModel


class Comment(BaseModel):
    id: str
    postID: str
    authorID: str
    parentID: str
    content: Any
    createdAt: Any
    updatedAt: datetime


class Post(BaseModel):
    id: str
    authorID: str
    title: Any
    slug: str
    content: Any
    excerpt: Any
    status: Any
    publishedAt: Any
    viewCount: int
    createdAt: Any
    updatedAt: datetime


class PostTag(BaseModel):
    postID: str
    tagID: str


class Tag(BaseModel):
    id: str
    name: Any
    slug: str
    description: Any


class User(BaseModel):
    id: str
    email: str
    phoneNumber: str | None = None
    passwordHash: Any
    name: Any
    role: Any
    avatarURL: str
    createdAt: Any
    updatedAt: datetime


class UserVault(BaseModel):
    id: str
    personalKey: str
    bio: str | None = None

