"""Repository port for CommentRepository generated by ANG."""

from __future__ import annotations

from typing import Any, Protocol


class CommentRepository(Protocol):
    async def count_by_post(self, *args: Any, **kwargs: Any) -> Any: ...
    async def delete_by_parent(self, *args: Any, **kwargs: Any) -> Any: ...
    async def delete_by_post(self, *args: Any, **kwargs: Any) -> Any: ...
    async def list_by_post(self, *args: Any, **kwargs: Any) -> Any: ...
