"""Repository port for PostRepository generated by ANG."""

from __future__ import annotations

from typing import Any, Protocol


class PostRepository(Protocol):
    async def count_published(self, *args: Any, **kwargs: Any) -> Any: ...
    async def count_published_by_tag(self, *args: Any, **kwargs: Any) -> Any: ...
    async def find_by_slug(self, *args: Any, **kwargs: Any) -> Any: ...
    async def list_by_author(self, *args: Any, **kwargs: Any) -> Any: ...
    async def list_by_author_and_status(self, *args: Any, **kwargs: Any) -> Any: ...
    async def list_published(self, *args: Any, **kwargs: Any) -> Any: ...
    async def list_published_by_tag(self, *args: Any, **kwargs: Any) -> Any: ...
