"""Blog routes generated by ANG."""

from __future__ import annotations

from typing import Any

from fastapi import APIRouter, Depends

from app import models
from app.services.blog import BlogService, get_blog_service

router = APIRouter(tags=["Blog"])

@router.delete("/comments/{id}")
async def delete_comment(id: str, svc: BlogService = Depends(get_blog_service)) -> models.DeleteCommentResponse:
    return await svc.delete_comment(id)

@router.delete("/posts/{id}")
async def delete_post(id: str, svc: BlogService = Depends(get_blog_service)) -> models.DeletePostResponse:
    return await svc.delete_post(id)

@router.delete("/tags/{id}")
async def delete_tag(id: str, svc: BlogService = Depends(get_blog_service)) -> models.DeleteTagResponse:
    return await svc.delete_tag(id)

@router.get("/my/posts")
async def list_my_posts(svc: BlogService = Depends(get_blog_service)) -> models.ListMyPostsResponse:
    return await svc.list_my_posts()

@router.get("/posts")
async def list_posts(svc: BlogService = Depends(get_blog_service)) -> models.ListPostsResponse:
    return await svc.list_posts()

@router.get("/posts/{postID}/comments")
async def list_comments(postID: str, svc: BlogService = Depends(get_blog_service)) -> models.ListCommentsResponse:
    return await svc.list_comments(postID)

@router.get("/posts/{slug}")
async def get_post(slug: str, svc: BlogService = Depends(get_blog_service)) -> models.GetPostResponse:
    return await svc.get_post(slug)

@router.get("/tags")
async def list_tags(svc: BlogService = Depends(get_blog_service)) -> models.ListTagsResponse:
    return await svc.list_tags()

@router.post("/posts")
async def create_post(payload: models.CreatePostRequest, svc: BlogService = Depends(get_blog_service)) -> models.CreatePostResponse:
    return await svc.create_post(payload)

@router.post("/posts/{id}/archive")
async def archive_post(id: str, payload: models.ArchivePostRequest, svc: BlogService = Depends(get_blog_service)) -> models.ArchivePostResponse:
    return await svc.archive_post(id, payload)

@router.post("/posts/{id}/publish")
async def publish_post(id: str, payload: models.PublishPostRequest, svc: BlogService = Depends(get_blog_service)) -> models.PublishPostResponse:
    return await svc.publish_post(id, payload)

@router.post("/posts/{id}/submit")
async def submit_post(id: str, payload: models.SubmitPostRequest, svc: BlogService = Depends(get_blog_service)) -> models.SubmitPostResponse:
    return await svc.submit_post(id, payload)

@router.post("/posts/{postID}/comments")
async def create_comment(postID: str, payload: models.CreateCommentRequest, svc: BlogService = Depends(get_blog_service)) -> models.CreateCommentResponse:
    return await svc.create_comment(postID, payload)

@router.post("/tags")
async def create_tag(payload: models.CreateTagRequest, svc: BlogService = Depends(get_blog_service)) -> models.CreateTagResponse:
    return await svc.create_tag(payload)

@router.put("/comments/{id}")
async def update_comment(id: str, payload: models.UpdateCommentRequest, svc: BlogService = Depends(get_blog_service)) -> models.UpdateCommentResponse:
    return await svc.update_comment(id, payload)

@router.put("/posts/{id}")
async def update_post(id: str, payload: models.UpdatePostRequest, svc: BlogService = Depends(get_blog_service)) -> models.UpdatePostResponse:
    return await svc.update_post(id, payload)

@router.put("/tags/{id}")
async def update_tag(id: str, payload: models.UpdateTagRequest, svc: BlogService = Depends(get_blog_service)) -> models.UpdateTagResponse:
    return await svc.update_tag(id, payload)

